mapper.update:
 	 update cart
	 set    quantity = #{quantity}
	 where  cart_no = #{cartNo}

mapper.select:
SELECT c.cart_no, c.quantity, c.pd_code, c.user_id, p.pd_name, -- 상품명 p.pd_price, -- 상품가격 p.pd_img, -- 상품이미지 c.quantity * p.pd_price AS total_price -- 상품수량 * 상품가격 FROM cart c JOIN product p ON c.pd_code = p.pd_code WHERE c.user_id = #{id}
AND c.cart_no IN
#{cartNo}
AND 1 = 0

mapper.delete:
DELETE FROM cart
WHERE user_id = #{userId}
  AND pd_code = #{pdCode}
  AND quantity = #{quantity}
